import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import DetailPresenter from "./DetailPresenter";
import { RootReducerType } from "../../reducers/rootReducer";

interface Props {
	match: {
		params: {
			id: string;
		};
	};
}

interface Card {
	workingCards: Array<{
		id: string;
		category: string;
		title: string;
		src: string;
		content: string;
		tags: Array<string>;
	}>;

	toyCards: Array<{
		id: string;
		category: string;
		title: string;
		src: string;
		content: string;
		tags: Array<string>;
	}>;
}

const DetailContainer = ({
	match: {
		params: { id },
	},
}: Props): JSX.Element => {
	const cardData: Card = useSelector(
		(state: RootReducerType) => state.cardReducer
	);

	const projectCards = cardData.workingCards.concat(cardData.toyCards);
	console.log(projectCards);

	useEffect(() => {
		const targetID = id;
		console.log(targetID);

		const targetIndex = projectCards.findIndex((el) => el.id === targetID);
		console.log(targetIndex);
	}, []);

	return <DetailPresenter />;
};

export default DetailContainer;
