import React from "react";
import { useSelector } from "react-redux";
import styled from "styled-components";
import shortid from "shortid";
import { BREAK_POINT_PC } from "Components/utilStyles";
import { RootReducerType } from "../reducers/rootReducer";

interface Link {
	aboutInfo: {
		link: Array<{
			category: string;
			categoryInfo: {
				address: string;
			};
		}>;
	};
}

const FooterWrap = styled.footer`
	width: 100vw;
	background-color: rgb(241, 243, 245);
`;

const FooterInner = styled.div`
	display: flex;
	align-items: center;
	min-height: 150px;
	margin: 0 auto;

	@media only screen and (min-width: ${BREAK_POINT_PC}px) {
		max-width: 1024px;
	}
`;

const LinkGroup = styled.div`
	display: flex;
	margin-left: auto;
`;

export default function Footer(): JSX.Element {
	const aboutData: Link = useSelector(
		(state: RootReducerType) => state.aboutReducer
	);

	// const linkInfo = aboutData.aboutInfo.link;
	// console.log(linkInfo);

	return (
		<FooterWrap>
			<FooterInner>
				<p>Copyright &copy; GyeongJinKwak All right reserved</p>
				<LinkGroup>
          {
            aboutData.aboutInfo.link.map((unit)=>
              <a href={unit.category} target="_blank" key={shortid.generate()}>
                <img
                  src={require("../assets/github.svg")}
                  alt="github"
                  className="about-link"
                />
              </a>
            )
          }
				</LinkGroup>
			</FooterInner>
		</FooterWrap>
	);
}
